const reviews=document.querySelector(".reviews"),reviewSlider=reviews.querySelectorAll(".reviews__slider"),reviewSliderGreen=reviews.querySelector(".reviews__slider-green"),reviewSliderGrey=reviews.querySelector(".reviews__slider-grey"),reviewSliderRed=reviews.querySelector(".reviews__slider-red"),button=document.querySelector(".trailer__video-play"),counter=reviews.querySelectorAll(".reviews__item-counter");function showGreen(e,r){let i=1;const t=.5*counter[e].textContent,l=setInterval((function(){i>=t?clearInterval(l):(i++,r.style.width=i+"px")}),10)}function showAll(){showGreen(0,reviewSliderGreen),showGreen(1,reviewSliderGrey),showGreen(2,reviewSliderRed)}button.addEventListener("click",showAll);